import React, { Component } from 'react';
import Child from './child'

class PracticeTwo extends Component {
    state = { 
        SayName:''
    }
    myInfo = (para) => {
        this.setState({
            SayName:para
        })
    }

    render() { 
        return ( 
            <div>
                <p> {this.state.SayName} </p>
                <Child passing={this.myInfo} />
            </div>
         );
    }
}

export default PracticeTwo;
