import React, { Component } from 'react';

class FormPractice extends Component {
    state = { 
        username:'',
        age:24
     }

     onChangeHandler = (event) => {
         let nameTarget = event.target.name;
         let setName = event.target.value;
         this.setState({
             [nameTarget]: setName
         })
     }

    render() { 
      const age = this.state.age;
      const updateName = this.state.username;
        return (
            <div>
                <form>
                    <p>Form Parctice</p>
                    <input name="Fname" onChange={this.onChangeHandler} type="text" placeholder="Name"></input>
                    <input name="username" onChange={this.onChangeHandler} type="text" placeholder="Name"></input>
                    <input name="username" onChange={this.onChangeHandler} type="text" placeholder="Name"></input>
                    <p>My Name is: {updateName}</p> 
                    <button>Click Me</button>
                </form>
            </div>
          );
    }
}

export default FormPractice;
