import React, { Component } from 'react';
import SubMain from './SubMain'

class Main extends Component {
    state = { 
        persons: [
            {name:'John', age:26},
            {name:'Habib', age:25},
            {name:'Tonoy', age:22}
        ],
        showPersons:false
     }

     clickHandler = () => {
         const doesShow = this.state.showPersons;
         this.setState({
             showPersons: !doesShow
         })
     }

    render() { 
        return ( 
           <div>
               <button onClick={this.clickHandler}>Click Me</button>
               { this.state.showPersons === true ? 
               <div>
               <SubMain name={this.state.persons[0].name} age={this.state.persons[0].age} />
               <SubMain name={this.state.persons[1].name} age={this.state.persons[1].age} />
               <SubMain name={this.state.persons[2].name} age={this.state.persons[2].age} />
               </div> : <div>
                   <h3>hello</h3>
               </div>
               }
           </div> 
         );
    }
}

export default Main;
