import React, { Component } from 'react';
import axios from 'axios';

class BookAdd extends Component {

    state = {
      book: [
        bookName = '',
        author ='',
        year ='',
      ],
     result:[]
    }



     changeHandler = (e) => {
         this.setState({
             [e.target.name]: e.target.value
         })
     }

     submitHandler = (e) => {
         e.preventDefault()
        axios.post('https://jsonplaceholder.typicode.com/posts', this.state)
        .then(response => {
            this.setState({
                result: [response.data]
            })
            console.log(this.state.result)
        })
        .catch(error => {
            console.log(error)
        })
     }

    render() { 
        const { bookName, author, year } = this.state;
        // const resultBook = this.state.result.bookName;
        // const resultAuthor = this.state.result.author;
        // const resultyear = this.state.result.year;
        // const anotherResult = this.state.resultTwo.bookName
        return ( 
            <div>
                <form onSubmit={this.submitHandler}>
                <div className="form-group">
    <label >Book Name</label>
    <input type="text" className="form-control" name="bookName" onChange={this.changeHandler} value={bookName} />
  </div>
  <div className="form-group">
    <label>Author</label>
    <input type="text" className="form-control" name="author" onChange={this.changeHandler} value={author} />
  </div>
  <div className="form-group">
    <label>year</label>
    <input type="number" className="form-control" name="year" onChange={this.changeHandler} value={year} />
  </div>
  <button type="submit" className="btn btn-primary">Submit</button>
                </form>

                <table className="table">
  <thead>
    <tr>
      <th scope="col">BookName</th>
      <th scope="col">Author</th>
      <th scope="col">Year</th>
    </tr>
  </thead>
  <tbody>
    <tr>

    {/* <td>{resultBook}</td>
        <td>{resultAuthor}</td>
        <td>{resultyear}</td> */}
    </tr>
    <tr>

       {/* <td>{}</td> */}
        {/* <td>{resultAuthor}</td> */}
        {/* <td>{resultyear}</td> */}
    </tr>
    <tr>

    {/* <td>{resultBook}</td>
        <td>{resultAuthor}</td>
        <td>{resultyear}</td> */}
    </tr>
  </tbody>
</table>
            </div>
         );
    }
}

export default BookAdd;
