import React, { Component } from 'react';
import axios from 'axios'

class PostApi extends Component {
    state = { 
        firstName:'',
        lastName:'',
        surName:''
     }

     onClickHandler = (e) => {
         this.setState({[e.target.name]: e.target.value})
     }

     onClickHandler = () => {

     }

    render() { 
        const {firstName, lastName, surName} = this.state
        return ( 
            <div>
                <form>
                <input name="firstName" onChange={this.onChangeHandler} type="text" placeholder="FirstName" value={firstName}></input>
                <input name="lastName" onChange={this.onChangeHandler} type="text" placeholder="LastName" value={lastName}></input>
                <input name="surName" onChange={this.onChangeHandler} type="text" placeholder="SurName" value={surName}></input>
                <button onClick={this.onClickHandler} >Submit</button>
                </form>
            </div>
         );
    }
}

export default PostApi;
